import{Cart}from"../models/Cart.js";import{CartController}from"./CartController.js";const ProductsGalleryController=(t,r)=>{const l=document.getElementById("gallery"),n=t,o=CartController(r),s=(t,r,l,n)=>`<div class="swiper-slide">\n        <article class="product-card">\n            <div class="productthumb">\n                <img\n                    class="product"\n                    src=${n}\n                    alt=${r}\n                />\n            </div>\n            <div class="product-label">\n                <button class="buttoncart" id=${t} >\n                    <i class="fa-solid fa-cart-plus"></i>\n                </button>\n                <h4 class="title">${r}</h4>\n                <span class="price">${l}</span>\n            </div>\n        </article>\n    </div>`,e=()=>{const t=Array.from(document.querySelectorAll(".buttoncart"));for(let l of t)l.addEventListener("click",()=>{const t=n.find(t=>t.id===+l.id);r.addItem(t),o.createList(r.getItemsList())})};(()=>{for(let t of n){const{id:r,name:n,price:o,img_url:e}=t;l.innerHTML+=s(r,n,o,e)}e()})()};export{ProductsGalleryController};