const ShowElementController=(e,t,l)=>{const n=document.getElementById(e),o=document.getElementById(t),d=document.getElementById(l);console.log(d);return{close:()=>{o.addEventListener("click",()=>{d.style.width="0",d.style.display="none"})},open:()=>{n.addEventListener("click",()=>{d.style.width="340px",d.style.display="block"})}}};export{ShowElementController};