const ShowElementController=(e,t,l)=>{const o=document.getElementById(e),n=document.getElementById(t),c=document.getElementById(l);console.log(c);return{close:()=>{n.addEventListener("click",()=>{console.log(c),c.style.width="0",c.style.display="none"})},open:()=>{o.addEventListener("click",()=>{console.log(c),c.style.width="300px",c.style.display="block"})}}};export{ShowElementController};